/*================================
	load js file from vegana app directory
	this file is placed in vegana_project_directory/js/some/random.js
================================*/
const load_native_vegana_wasm_module = await engine.loader.load.js({
	id;'any_id',//this is not required or is elective
	type:'local',
	url:'some/random.js'
})
.then(()=>{
	return true;
})
.catch(()=>{
	return false;
});

/*================================
	load external js file
================================*/
const load_external_wasm_module = await engine.loader.load.js({
	type:'url',
	url:'https://some.com/some.js'
})
.then(()=>{
	return true;
})
.catch(()=>{
	return false;
});


/*================================
	input object schema
================================*/
{
	id:{type:'string',elective:true},
	type:{type:'string',options:['local','url']},
	url:{type:'string',max:4048},
	module:{type:'boolean',elective:true}        
}
