const parent_div = engine.make.div({
	parent:pageId,
    draw:{
    	all:{
        	border:"5px solid purple",
            padding:"10px",
            "grid-template-columns":"50% 50%",
            display:"grid"
        }
    }
});

//local image

const local_image_div = engine.make.div({
	parent:parent_div,
    draw:{
    	all:{
        	border:"5px solid pink",
            padding:"10px",
        }
    }
});

  engine.make.image({
      parent:local_image_div,
      type:"local",
      location:"assets/images/logo-mix.png",
      draw:{
          all:{
              height:"100px",
              width:"200px"
          }
      }
  });

//from a link

const url_image_div = engine.make.div({
	parent:parent_div,
    draw:{
    	all:{
        	border:"5px solid blue",
            padding:"10px",
        }
    }
});

  const href = engine.make.a({
      parent:url_image_div,
      type:'url',
      href:'https://www.mozilla.org',
	  text:'visit mozzila org and show your support.',
      target:'_blank'
  });

    engine.make.image({
        parent:href,
        type:"url",
        location:"https://www.mozilla.org/media/protocol/img/logos/firefox/logo-lg-high-res.7ba3ce88e665.png",
        draw:{
          all:{
              height:"100px",
              width:"100px"
          }
        }
    });
