data.value:
const parent_div = engine.make.div({
	parent:pageId,
    draw:{
    	all:{
        	border:"5px solid purple",
            padding:"10px"
        }
    }
});

    const value_div = engine.make.div({
        parent:parent_div,
        text:'selected value will be displayed here.',
        draw:{
            all:{
                border:"5px solid pink",
                padding:"10px"
            }
        }
    });

    let input_draw = {
    	all:{
        	display:'block',
            border:"5px solid pink",
            width:'100%',
            padding:"10px",
            'outline':'none'
        }
    };

     let input_function =  (id,value)=>{
     	if(typeof(value) !== "string"){
        	value = String(value);
        }
     	engine.set.div.text(value_div,`selected value : ${value}`);
     }  

	engine.make.input({
    	parent:parent_div,
        type:"string",
        placeholder:'string input',
        draw:input_draw,
        function:input_function
    });

    engine.make.input({
    	parent:parent_div,
        type:"number",
        placeholder:'number input',
        draw:input_draw,
        function:input_function
    });

    engine.make.input({
    	parent:parent_div,
        type:"email",
        placeholder:'email input',
        draw:input_draw,
        function:input_function
    });

    engine.make.input({
    	parent:parent_div,
        type:"file",
        draw:input_draw,
        function:(id,files)=>{
            engine.set.div.text(value_div,`selected file : ${files[0].name}`);
        }
    });

    engine.make.input({
    	parent:parent_div,
        type:"checkbox",
        draw:{
        	all:{
            	margin:"10px"
            }
        },
        function:(id,checked)=>{
            engine.set.div.text(value_div,`selected value : checked => ${checked}`);
        }
    });
