const parent_div = engine.make.div({
	parent:pageId,
    draw:{
    	all:{
        	border:"5px solid purple",
            padding:"10px"
        }
    }
});

    const value_div = engine.make.div({
        parent:parent_div,
        text:'selected value will be displayed here.',
        draw:{
            all:{
                border:"5px solid pink",
                padding:"10px"
            }
        }
    });

	engine.make.select({
    	parent:parent_div,
        options:[
        	{
            	text:"one",
                value:"one_value",
                class:"page-docs-cont-make-panel-select-option_class"
            },
            {
            	text:"two",
                value:"two_value",
                disabled:true
            },
            {text:'three',value:"three_value"},
            {text:'four',value:"four_value"},
            {text:'five',value:"five_value"},
        ],
        draw:{
            all:{
            	display:'block',
                border:"5px solid pink",
                width:'100%',
                padding:"10px"
            }
        },
        function:(id,value)=>{
        	engine.set.div.text(value_div,`selected value : ${value}`);
        }
    });
