	//router params

    engine.router.init.comps(parent_element_id_string,comp_module_object,{data_to_pass},"router_class_string")

*/

const firstComp = require("./comps/firstComp/comp");
const secondComp = require("./comps/secondComp/comp");

let router_id = engine.router.init.comps(pageId,firstComp,{some:true},"router-class");

//-----------------------------------
//	route to new comp
//-----------------------------------
/*

	//navigator params

    engine.router.navigate.to.comp(comp_module_object,{data_to_pass},router_is_string)

*/

//route to prebuilt or new comp module
engine.router.navigate.to.comp(secondComp,{data_to_pass},router_id);

