//-----------------------------------
//	make cont router
//-----------------------------------
/*

	this router should be made on a page module only.

	router params

    engine.router.init.conts(page_id_string,router_class_string);

*/

engine.router.init.conts(pageId,"conts-router-class");

//-----------------------------------
//	integrate cont
//-----------------------------------
/*

	the cont module will integrate automatically once you require it in your page module.

    cont module should only be loaded in there parent page.

*/


require("./conts/firstCont/cont");

//-----------------------------------
// integrate lazy cont
//-----------------------------------
/*

	lazy cont first are neede to be loaded on to the app.

*/

const loadFirstCont = await engine.loader.load.cont("firstPage","firstCont")
.then(()=>{
	return true;
})
.catch(()=>{
	return false;
});

//-----------------------------------
// get cont modules
//-----------------------------------
/*

	call the contModule sub-api in get api of engine to get contModule.

*/

let firstCont = engine.get.contModule("firstPage","firstCont");

//-----------------------------------
//	route to new cont
//-----------------------------------
/*

	//navigator params

    engine.router.navigate.to.cont(cont_module_object,{data_to_pass})

*/

//route to prebuilt or new cont module
engine.router.navigate.to.cont(firstCont,{data_to_pass});

//rebuild page module
//this api removes the old cont module body and makes a new one.
engine.router.navigate.new.cont(firstCont,{data_to_pass});

