//-----------------------------------
//	make page module
//-----------------------------------
/*

	//you can make page module with vegana cli
    //for more information see the cli documents

    $ vegana generate page first

    $ vegana generate page second

*/

//-----------------------------------
//	integrate page
//-----------------------------------
/*

	the page module will integrate automatically once you require it in your page.

*/


require("./pages/firstPage/page");

//-----------------------------------
// integrate lazy page
//-----------------------------------
/*

	lazy page first are neede to be loaded on to the app index.

*/

const loadFirstPage = await engine.loader.load.page("firstPage")
.then(()=>{
	return true;
})
.catch(()=>{
	return false;
});

//-----------------------------------
// get page modules
//-----------------------------------
/*

	call the pageModule sub-api in get api of engine to get pageModule.

*/

let firstPage = engine.get.pageModule("firstPage");

//-----------------------------------
//	route to new page
//-----------------------------------
/*

	//navigator params

    engine.router.navigate.to.page(page_module_object,{data_to_pass})

*/

//route to prebuilt or new page module
engine.router.navigate.to.page(firstPage,{data_to_pass});

//rebuild page module
//this api removes the old page module body and makes a new one
engine.router.navigate.new.page(firstPage,{data_to_pass});

